mapper.select:
select p.productId, pt.productOrderId, pt.productOrderCode, pt.productOrderPayDate, p.productName, p.productSalePrice, poi.productOrderItemNumber, poi.productOrderItemPrice, pt.productOrderStatus from productorder pt left join productorderitem poi on poi.productOrderItemOrderId = pt.productOrderId left join product p on poi.productOrderItemProductId = p.productId
p.productIsEnabled != 1 and pt.productOrderUserId = #{userId}
and productName like concat('%', #{productName}, '%')

select p.productId, pt.productOrderId, pt.productOrderCode, pt.productOrderPayDate, p.productName, p.productSalePrice, poi.productOrderItemNumber, poi.productOrderItemPrice, pt.productOrderStatus from productorder pt left join productorderitem poi on poi.productOrderItemOrderId = pt.productOrderId left join product p on poi.productOrderItemProductId = p.productId
p.productIsEnabled != 1 and pt.productOrderUserId = #{userId} and pt.productOrderStatus = #{type}
and productName like concat('%', #{productName}, '%')
